    <>
      <Row>
        <H1>Najnowsze produkty:</H1>
        {isLoading && <h1>Ładowanie...</h1>}
        {products.map((product) => (
          <Col sm={12} md={6} lg={4} xl={3} key={product._id}>
            <Product product={product} />
          </Col>
        ))}
      </Row>
    </>
  );
};
export default HomeScreen;
